CREATE TABLE user_answers ( id BIGINT AUTO_INCREMENT PRIMARY KEY, user_id BIGINT NOT NULL, question_id BIGINT NOT NULL, selected_option_id BIGINT, is_correct BOOLEAN NOT NULL, answered_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, FOREIGN KEY (user_id) REFERENCES users(id), FOREIGN KEY (question_id) REFERENCES questions(id), FOREIGN KEY (selected_option_id) REFERENCES question_options(id) );
